package ionuth.test.others;

import org.apache.commons.lang3.StringUtils;

public class App {

    public String getGreeting() {
        var str = "aaabbb";
        str = StringUtils.upperCase(str);
        return "Hello " + str;
    }

    public static void main(String[] args) {
        System.out.println("");
        System.out.println(new App().getGreeting());
        System.out.println("");
    }
}
